                    // 引入 Chart.js 库
                    var ctx = document.getElementById('myPieChart').getContext('2d');
                    var pieChart = new Chart(ctx, {
                        type: 'pie',
                        data: {
                            labels: res.offer,
                            datasets: [{
                                data: res.total_quantity,
                                backgroundColor: ['red', 'blue', 'green', 'yellow'],
                            }]
                        },
                        options: {
                            responsive: true,
